import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import { SocketProvider } from './contexts/SocketContext';

// Components
import Navbar from './components/layout/Navbar';
import Footer from './components/layout/Footer';
import PrivateRoute from './components/common/PrivateRoute';
import LoadingSpinner from './components/common/LoadingSpinner';

// Pages
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Tournaments from './pages/tournaments/Tournaments';
import TournamentDetails from './pages/tournaments/TournamentDetails';
import CreateTournament from './pages/tournaments/CreateTournament';
import Teams from './pages/teams/Teams';
import TeamDetails from './pages/teams/TeamDetails';
import CreateTeam from './pages/teams/CreateTeam';
import MatchSearch from './pages/matches/MatchSearch';
import SimpleMatchDetails from './pages/matches/SimpleMatchDetails';
import NotFound from './pages/NotFound';

const App = () => {
  const { loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <SocketProvider>
      <div className="app">
        <Navbar />
        <main className="main-content">
          <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          <Route path="/dashboard" element={
            <PrivateRoute>
              <Dashboard />
            </PrivateRoute>
          } />

          <Route path="/tournaments" element={<Tournaments />} />
          <Route path="/tournaments/:id" element={<TournamentDetails />} />
          <Route path="/tournaments/create" element={
            <PrivateRoute>
              <CreateTournament />
            </PrivateRoute>
          } />

          <Route path="/teams" element={<Teams />} />
          <Route path="/teams/:id" element={<TeamDetails />} />
          <Route path="/teams/create" element={
            <PrivateRoute>
              <CreateTeam />
            </PrivateRoute>
          } />

          <Route path="/matches/search" element={<MatchSearch />} />
          <Route path="/matches/:id" element={<SimpleMatchDetails />} />

          <Route path="*" element={<NotFound />} />
        </Routes>
        </main>
        <Footer />
      </div>
    </SocketProvider>
  );
};

export default App;
