import React, { createContext, useContext, useState, useEffect } from 'react';
import io from 'socket.io-client';

// Create the socket context
const SocketContext = createContext();

// Socket provider component
export const SocketProvider = ({ children }) => {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [joinedMatches, setJoinedMatches] = useState([]);

  // Initialize socket connection
  useEffect(() => {
    // Get base URL from environment or use default
    const baseUrl = process.env.REACT_APP_SOCKET_URL || window.location.origin;

    // Create socket instance
    const socketInstance = io(baseUrl, {
      autoConnect: true,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000
    });

    // Socket event handlers
    socketInstance.on('connect', () => {
      console.log('Socket connected');
      setIsConnected(true);

      // Rejoin any previously joined match rooms
      joinedMatches.forEach(matchId => {
        socketInstance.emit('join-match', { matchId });
      });
    });

    socketInstance.on('disconnect', () => {
      console.log('Socket disconnected');
      setIsConnected(false);
    });

    socketInstance.on('connect_error', (error) => {
      console.error('Socket connection error:', error);
    });

    // Save socket instance
    setSocket(socketInstance);

    // Cleanup on unmount
    return () => {
      if (socketInstance) {
        console.log('Disconnecting socket');
        socketInstance.disconnect();
      }
    };
  }, []); // Empty dependency array to avoid re-creating socket

  // Join a match room to receive updates
  const joinMatch = (matchId) => {
    if (!matchId) return;

    console.log(`Joining match room: ${matchId}`);

    if (socket) {
      socket.emit('join-match', { matchId });
    }

    // Track joined matches for reconnection - use functional update to avoid stale state
    setJoinedMatches(prevMatches => {
      if (!prevMatches.includes(matchId)) {
        return [...prevMatches, matchId];
      }
      return prevMatches;
    });
  };

  // Leave a match room
  const leaveMatch = (matchId) => {
    if (!matchId) return;

    console.log(`Leaving match room: ${matchId}`);

    if (socket) {
      socket.emit('leave-match', { matchId });
    }

    // Update joined matches list - use functional update to avoid stale state
    setJoinedMatches(prevMatches => prevMatches.filter(id => id !== matchId));
  };

  // Socket context value
  const contextValue = {
    socket,
    isConnected,
    joinMatch,
    leaveMatch,
    // Event subscription helpers
    subscribeToEvent: (event, callback) => {
      if (socket) {
        socket.on(event, callback);
        return () => socket.off(event, callback);
      }
      return () => {};
    },
    emitEvent: (event, data) => {
      if (socket) {
        socket.emit(event, data);
      }
    }
  };

  return (
    <SocketContext.Provider value={contextValue}>
      {children}
    </SocketContext.Provider>
  );
};

// Custom hook to use the socket context
export const useSocket = () => {
  const context = useContext(SocketContext);
  if (!context) {
    console.warn('useSocket must be used within a SocketProvider');
    // Return mock implementation to prevent errors
    return {
      socket: null,
      isConnected: false,
      joinMatch: () => {},
      leaveMatch: () => {},
      subscribeToEvent: () => () => {},
      emitEvent: () => {}
    };
  }
  return context;
};
