import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import tournamentApi from '../../services/tournamentApi'; // Updated import
import LoadingSpinner from '../../components/common/LoadingSpinner';

const Tournaments = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        setLoading(true);
        const response = await tournamentApi.getAllTournaments();
        setTournaments(response.data || []);
      } catch (error) {
        console.error('Error fetching tournaments:', error);
        setError('Failed to load tournaments');
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, []);

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="px-4 py-6">
      <div className="container mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold text-light-100">Tournaments</h1>
          <Link to="/tournaments/create" className="btn btn-primary">Create Tournament</Link>
        </div>

        {error && <div className="error-message">{error}</div>}

        {tournaments.length === 0 ? (
          <div className="text-center py-16">
            <p className="mb-4 text-light-200">No tournaments found. Create your first tournament to get started.</p>
            <Link to="/tournaments/create" className="btn btn-primary">Create Tournament</Link>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {tournaments.map(tournament => (
              <div key={tournament.id} className="card bg-dark-200 p-6 rounded-lg shadow-game border border-dark-100">
                <h2 className="text-xl font-bold mb-2 text-primary">{tournament.name}</h2>
                <div className="mb-2 text-light-200 text-sm">
                  <span>{new Date(tournament.start_date || tournament.startDate).toLocaleDateString()}</span>
                  <span> to </span>
                  <span>{new Date(tournament.end_date || tournament.endDate).toLocaleDateString()}</span>
                </div>
                <p className="mb-3 text-light-100">{tournament.format} | {tournament.scoring_system || tournament.scoringSystem}</p>
                {tournament.description && (
                  <p className="mb-4 text-light-200 text-sm">{tournament.description}</p>
                )}
                <Link to={`/tournaments/${tournament.id}`} className="btn btn-outline">View Details</Link>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Tournaments;
