import axios from 'axios';
import authService from './authService';

/**
 * Service for team-related API calls
 */
const teamService = {
  /**
   * Get all teams
   * @param {Object} options - Query options
   * @returns {Promise} Promise with teams data
   */
  async getTeams(options = {}) {
    const { limit = 20, offset = 0, search } = options;

    let query = `/api/teams?limit=${limit}&offset=${offset}`;
    if (search) query += `&search=${encodeURIComponent(search)}`;

    const response = await axios.get(query);
    return response.data;
  },

  /**
   * Get team by ID
   * @param {string} id - Team ID
   * @returns {Promise} Promise with team data
   */
  async getTeam(id) {
    const response = await axios.get(`/api/teams/${id}`);
    return response.data;
  },

  /**
   * Create new team
   * @param {Object} teamData - Team data
   * @returns {Promise} Promise with created team
   */
  async createTeam(teamData) {
    const token = authService.getToken();

    if (!token) {
      throw new Error('Authentication required');
    }

    const response = await axios.post('/api/teams', teamData, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    return response.data;
  },

  /**
   * Update team
   * @param {string} id - Team ID
   * @param {Object} teamData - Team data to update
   * @returns {Promise} Promise with updated team
   */
  async updateTeam(id, teamData) {
    const token = authService.getToken();

    if (!token) {
      throw new Error('Authentication required');
    }

    const response = await axios.put(`/api/teams/${id}`, teamData, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    return response.data;
  },

  /**
   * Delete team
   * @param {string} id - Team ID
   * @returns {Promise} Promise with response data
   */
  async deleteTeam(id) {
    const token = authService.getToken();

    if (!token) {
      throw new Error('Authentication required');
    }

    const response = await axios.delete(`/api/teams/${id}`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    return response.data;
  },

  /**
   * Get team players
   * @param {string} id - Team ID
   * @returns {Promise} Promise with team players
   */
  async getTeamPlayers(id) {
    const response = await axios.get(`/api/teams/${id}/players`);
    return response.data;
  },

  /**
   * Add players to team
   * @param {string} id - Team ID
   * @param {Array} players - Array of player objects
   * @returns {Promise} Promise with response data
   */
  async addPlayersToTeam(id, players) {
    const token = authService.getToken();

    if (!token) {
      throw new Error('Authentication required');
    }

    const response = await axios.post(`/api/teams/${id}/players`, 
      { players },
      {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }
    );

    return response.data;
  },

  /**
   * Remove player from team
   * @param {string} id - Team ID
   * @param {string} playerId - Player ID to remove
   * @returns {Promise} Promise with response data
   */
  async removePlayerFromTeam(id, playerId) {
    const token = authService.getToken();

    if (!token) {
      throw new Error('Authentication required');
    }

    const response = await axios.delete(`/api/teams/${id}/players/${playerId}`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    return response.data;
  },

  /**
   * Search players by PUBG name
   * @param {string} query - Search query
   * @param {string} platform - PUBG platform (default: 'steam')
   * @returns {Promise} Promise with search results
   */
  async searchPlayers(query, platform = 'steam') {
    if (!query || query.length < 3) {
      throw new Error('Search query must be at least 3 characters');
    }

    const response = await axios.get(`/api/players/search?query=${encodeURIComponent(query)}&platform=${platform}`);
    return response.data;
  }
};

export default teamService;
