import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlayerPerformance = ({ matchId, accountId }) => {
  const [playerData, setPlayerData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPlayerData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/telemetry/${matchId}/player/${accountId}`);
        setPlayerData(response.data.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching player performance data:', err);
        setError('Failed to load player data');
      } finally {
        setLoading(false);
      }
    };

    if (matchId && accountId) {
      fetchPlayerData();
    }
  }, [matchId, accountId]);

  if (loading) {
    return <div className="player-loading">Loading player data...</div>;
  }

  if (error) {
    return <div className="player-error">{error}</div>;
  }

  if (!playerData || !playerData.player) {
    return <div className="player-no-data">No player data available</div>;
  }

  const { player, team, events } = playerData;

  // Calculate additional stats
  const damageDealt = player.damage || 0;
  const totalDamageTaken = events.damageTaken.reduce((sum, event) => sum + event.damage, 0);
  const headshots = events.kills.filter(kill => kill.damageTypeCategory === 'HeadShot').length;
  const headshotPercentage = player.kills ? (headshots / player.kills) * 100 : 0;

  // Get weapon breakdown
  const weaponBreakdown = events.kills.reduce((acc, kill) => {
    const weapon = kill.damageCauserName?.split('_').pop() || 'Unknown';
    acc[weapon] = (acc[weapon] || 0) + 1;
    return acc;
  }, {});

  // Sort weapons by kill count
  const sortedWeapons = Object.entries(weaponBreakdown)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5); // Top 5 weapons

  return (
    <div className="player-performance-container">
      <div className="player-header">
        <h3>{player.name} - Match Performance</h3>
        <div className="team-info">
          Team: {team?.players?.join(', ') || 'Unknown'} (Rank: #{team?.rank || '?'})
        </div>
      </div>

      <div className="stats-container">
        <div className="stats-column">
          <div className="stat-box">
            <h4>Combat Stats</h4>
            <div className="stat-grid">
              <div className="stat-item">
                <div className="stat-label">Kills</div>
                <div className="stat-value">{player.kills}</div>
              </div>
              <div className="stat-item">
                <div className="stat-label">Damage</div>
                <div className="stat-value">{Math.round(damageDealt)}</div>
              </div>
              <div className="stat-item">
                <div className="stat-label">Damage Taken</div>
                <div className="stat-value">{Math.round(totalDamageTaken)}</div>
              </div>
              <div className="stat-item">
                <div className="stat-label">Headshots</div>
                <div className="stat-value">{headshots} ({headshotPercentage.toFixed(1)}%)</div>
              </div>
              <div className="stat-item">
                <div className="stat-label">Revives</div>
                <div className="stat-value">{player.revives}</div>
              </div>
              <div className="stat-item">
                <div className="stat-label">Heals</div>
                <div className="stat-value">{Math.round(player.heals)}</div>
              </div>
            </div>
          </div>
        </div>

        <div className="stats-column">
          <div className="stat-box">
            <h4>Top Weapons</h4>
            {sortedWeapons.length > 0 ? (
              <div className="weapon-list">
                {sortedWeapons.map(([weapon, kills], index) => (
                  <div key={index} className="weapon-item">
                    <div className="weapon-name">{formatWeaponName(weapon)}</div>
                    <div className="weapon-kills">{kills} kills</div>
                    <div className="weapon-bar" style={{ width: `${(kills / player.kills) * 100}%` }}></div>
                  </div>
                ))}
              </div>
            ) : (
              <p>No weapon data available</p>
            )}
          </div>
        </div>
      </div>

      <div className="combat-timeline">
        <h4>Combat Timeline</h4>
        {events.kills.length > 0 ? (
          <div className="combat-events">
            {/* Sort kills by time */}
            {events.kills
              .sort((a, b) => new Date(a.time) - new Date(b.time))
              .map((kill, index) => (
                <div key={index} className="combat-event">
                  <div className="event-time">
                    {new Date(kill.time).toISOString().substr(11, 8)}
                  </div>
                  <div className="event-icon">☠️</div>
                  <div className="event-details">
                    <strong>{player.name}</strong> killed <strong>{kill.victimName}</strong> with {formatWeaponName(kill.damageCauserName)}
                  </div>
                </div>
              ))
            }
          </div>
        ) : (
          <p>No combat events recorded</p>
        )}
      </div>

      <div className="damage-breakdown">
        <h4>Damage Exchanges</h4>
        <div className="damage-charts">
          <div className="damage-dealt">
            <h5>Damage Dealt</h5>
            {events.damage.length > 0 ? (
              <table className="damage-table">
                <thead>
                  <tr>
                    <th>Target</th>
                    <th>Damage</th>
                    <th>Hits</th>
                  </tr>
                </thead>
                <tbody>
                  {/* Group damage by victim */}
                  {Object.entries(events.damage.reduce((acc, event) => {
                    const victim = event.victimName;
                    if (!acc[victim]) {
                      acc[victim] = { damage: 0, hits: 0 };
                    }
                    acc[victim].damage += event.damage;
                    acc[victim].hits += 1;
                    return acc;
                  }, {}))
                    .sort((a, b) => b[1].damage - a[1].damage)
                    .map(([victim, data], index) => (
                      <tr key={index}>
                        <td>{victim}</td>
                        <td>{Math.round(data.damage)}</td>
                        <td>{data.hits}</td>
                      </tr>
                    ))
                  }
                </tbody>
              </table>
            ) : (
              <p>No damage data available</p>
            )}
          </div>

          <div className="damage-taken">
            <h5>Damage Taken</h5>
            {events.damageTaken.length > 0 ? (
              <table className="damage-table">
                <thead>
                  <tr>
                    <th>From</th>
                    <th>Damage</th>
                    <th>Hits</th>
                  </tr>
                </thead>
                <tbody>
                  {/* Group damage by attacker */}
                  {Object.entries(events.damageTaken.reduce((acc, event) => {
                    const attacker = event.attackerName;
                    if (!acc[attacker]) {
                      acc[attacker] = { damage: 0, hits: 0 };
                    }
                    acc[attacker].damage += event.damage;
                    acc[attacker].hits += 1;
                    return acc;
                  }, {}))
                    .sort((a, b) => b[1].damage - a[1].damage)
                    .map(([attacker, data], index) => (
                      <tr key={index}>
                        <td>{attacker}</td>
                        <td>{Math.round(data.damage)}</td>
                        <td>{data.hits}</td>
                      </tr>
                    ))
                  }
                </tbody>
              </table>
            ) : (
              <p>No damage taken data available</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Helper function to format weapon names
const formatWeaponName = (weaponId) => {
  if (!weaponId) return 'Unknown';

  // Extract the base name from the full weapon ID
  const baseName = weaponId.split('_').pop()?.replace('WeapGun', '')?.replace('Weap', '') || weaponId;

  // Format common weapon names
  const weaponMap = {
    'M416': 'M416',
    'SCAR-L': 'SCAR-L',
    'AK47': 'AKM',
    'HK416': 'M416',
    'M16A4': 'M16A4',
    'Kar98k': 'Kar98k',
    'SKS': 'SKS',
    'Mini14': 'Mini-14',
    'VSS': 'VSS',
    'DP28': 'DP-28',
    'M249': 'M249',
    'UMP': 'UMP',
    'Vector': 'Vector',
    'UZI': 'Micro UZI',
    'Thompson': 'Tommy Gun',
    'Win94': 'Win94',
    'Mk14': 'Mk14 EBR',
    'AWM': 'AWM',
    'M24': 'M24',
    'S12K': 'S12K',
    'S1897': 'S1897',
    'S686': 'S686',
    'Crossbow': 'Crossbow',
    'Punch': 'Fists',
    'Pan': 'Pan',
    'Machete': 'Machete',
    'Crowbar': 'Crowbar',
    'Sickle': 'Sickle',
    'Grenade': 'Frag Grenade',
    'Molotov': 'Molotov Cocktail',
    'Smoke': 'Smoke Grenade',
    'Stun': 'Stun Grenade',
    'RedZone': 'Red Zone',
    'Bluezone': 'Blue Zone',
    'Vehicle': 'Vehicle Crash'
  };

  return weaponMap[baseName] || baseName;
};

export default PlayerPerformance;
